import React from "react";
import PropTypes from "prop-types";

import { Avatar, Div, Panel, PanelHeader } from "@vkontakte/vkui";
import { Range } from "react-range";

import "./Home.css";
import logo from "../img/Logo.png";
import warning from "../img/Union.svg";

const STEP = 0.1;
const MIN = 0;
const MAX = 100;
let state = {
  values: [50],
};

const Home = ({}) => (
  <div className="main">
    <div className="header">
      <img src={logo} className="header__logo"></img>
      <div className="header__content">Бесплатный подбор займов</div>
    </div>
    <div className="block">
      <Range
        values={state.values}
        step={STEP}
        min={MIN}
        max={MAX}
        onChange={(values) => this.setState({ values })}
        renderTrack={({ props, children }) => (
          <div
            onMouseDown={props.onMouseDown}
            onTouchStart={props.onTouchStart}
            style={{
              ...props.style,
              height: "36px",
              display: "flex",
              width: "100%",
            }}
          >
            <div
              ref={props.ref}
              style={{
                height: "5px",
                width: "100%",
                borderRadius: "4px",
                background: getTrackBackground({
                  values: this.state.values,
                  colors: ["#548BF4", "#ccc"],
                  min: MIN,
                  max: MAX,
                }),
                alignSelf: "center",
              }}
            >
              {children}
            </div>
          </div>
        )}
        renderThumb={({ props, isDragged }) => (
          <div
            {...props}
            style={{
              ...props.style,
              height: "42px",
              width: "42px",
              borderRadius: "4px",
              backgroundColor: "#FFF",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              boxShadow: "0px 2px 6px #AAA",
            }}
          >
            <div
              style={{
                height: "16px",
                width: "5px",
                backgroundColor: isDragged ? "#548BF4" : "#CCC",
              }}
            />
          </div>
        )}
      />

      <div className="block-form">
        <div className="block-form__title">Сумма кредита</div>
        <div className="block-form__price">
          <span className="block-form__price-text">30 000 ₽</span>
        </div>
        <div className="block-form__slidecontainer">
          <input
            type="range"
            min="5000"
            max="250000"
            step="1000"
            className="slider"
          />
        </div>
        <div className="block-form__price-range">
          <span className="price__range-item">от 5 000 ₽</span>
          <span className="price__range-item">до 100 000 ₽</span>
        </div>
        <form action="./form.html" className="block-form__btnform">
          <input
            type="submit"
            value="Подобрать займ"
            className="block-form__button"
          />
        </form>
        <div className="block-form__desc">
          <img src={warning} alt="warning" className="intro__desc-logo" />
          <div className="block__desc-text">
            Финбокс позволяет получить займ и оценить качество работы МФО и МКК
          </div>
        </div>
      </div>
    </div>
  </div>
);

Home.propTypes = {
  id: PropTypes.string.isRequired,
};

export default Home;
