import React, { useState } from "react";
import PropTypes from "prop-types";

import "../panels/Comments.css";
import MemberLogo from "../img/memberLogo.svg";
import Back from "../img/chevron-back.svg";
import Rating from "@material-ui/lab/Rating";
import { withStyles } from "@material-ui/core/styles";

const Offers = (props) => {
  const [review, addReview] = useState();
  const [value, setValue] = useState(2);

  const InactiveStar = (props) => (
    <svg
      width={27}
      height={25}
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
      {...props}
    >
      <path
        d="m13.5.5 4.364 7.493 8.475 1.835-5.777 6.466.873 8.628-7.935-3.497-7.935 3.497.873-8.628L.661 9.828l8.475-1.835L13.5.5Z"
        fill="#EEF0F3"
      />
    </svg>
  );

  const ActiveStar = (props) => (
    <svg
      width={27}
      height={25}
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
      {...props}
    >
      <path
        d="m13.5.5 4.364 7.493 8.475 1.835-5.777 6.466.873 8.628-7.935-3.497-7.935 3.497.873-8.628L.661 9.828l8.475-1.835L13.5.5Z"
        fill="#FFA735"
      />
    </svg>
  );

  const StyledRating = withStyles({
    iconFilled: {
      color: "#fffff5",
    },
    iconHover: {
      color: "#ff3d47",
    },
  })(Rating);

  const AddComment = (event) => {
    event.preventDefault();
    let text = event.target[5].value;
    let rating = 0;
    let date = new Date(Math.floor(Date.now() / 1000) * 1000);
    let month = [
      "Январь",
      "Февраль",
      "Март",
      "Апрель",
      "Май",
      "Июнь",
      "Июль",
      "Август",
      "Сентябрь",
      "Ноябрь",
      "Декабрь",
    ];
    for (let i = 0; i <= 4; i++) {
      if (event.target[i].checked == true) {
        rating = i + 1;
      }
    }

    let comment = {
      name: "Евгений П.",
      body: text,
      time: date.getDate() + month[date.getMonth],
      rating: rating,
    };
    props.comments.push(comment);
    console.log(props.comments);
    saveComments();
  };

  const saveComments = () => {
    localStorage.setItem("comments", JSON.stringify(props.comments));
  };
  return (
    <div className="comments">
      <div className="comments__header">
        <img
          src={Back}
          alt="Back"
          className="header-back"
          onClick={props.go}
          data-to="offers"
        />
        <div className="member-logo">
          <img src={MemberLogo} alt="Logo" className="member-logo-img" />
        </div>
        <div className="member-info">
          <div className="member-title">Займер</div>
          <div className="member-about">
            <div className="member-reviews">12 отзывов</div>
            <div className="member-delimetr"></div>
            <div className="member-representatives">Есть представители</div>
          </div>
        </div>
      </div>
      <div className="comments__main">
        {props.comments.length >= 1
          ? props.comments.map((comment, index) => (
              <div className="comments__item">
                <div className="comments__item-avatar"></div>
                <div className="comments__item-other">
                  <div className="comments__item-name">{comment.name}</div>
                  <div className="comments__item-dateAndRate">
                    <StyledRating
                      name="customized-color"
                      defaultValue={comment.rating}
                      emptyIcon={<InactiveStar />}
                      icon={<ActiveStar />}
                      precision={1}
                      className="comments__item-rating"
                      size="2px"
                      readOnly
                    />
                    <div className="comments__item-date">{comment.time}</div>
                  </div>
                  <div className="comments__item-text">{comment.body}</div>
                </div>
              </div>
            ))
          : ""}
      </div>
      <div className="comments__footer">
        <input
          type="button"
          value="Оставить отзыв"
          className="comments__footer-btn"
          onClick={() => addReview(review === "active" ? "inactive" : "active")}
        />
      </div>
      <form className={`comments__feedback ${review}`} onSubmit={AddComment}>
        <div className="feedback-title">
          Ваша оценка и комментарий <br /> о компании Займер
        </div>
        <div className="feedback-rating">
          <StyledRating
            name="customized-color"
            defaultValue={0}
            emptyIcon={<InactiveStar />}
            icon={<ActiveStar />}
            precision={1}
            className="feedback-rating-item"
          />
        </div>
        <div className="feedback-textarea">
          <textarea className="feedback-textarea-input"></textarea>
        </div>
        <div className="feedback-submit">
          <input
            type="submit"
            value="Отправить"
            className="feedback-submit-btn"
          />
        </div>
        <div className="feedback-cancel">
          <input
            type="button"
            value="Отменить"
            className="feedback-cancel-btn"
            onClick={() =>
              addReview(review === "active" ? "inactive" : "active")
            }
          />
        </div>
      </form>
    </div>
  );
};

Offers.propTypes = {
  id: PropTypes.string.isRequired,
  go: PropTypes.func.isRequired,
};

export default Offers;
