import React, { useState, useRef, useEffect, useContext } from "react";
import PropTypes from "prop-types";

import "./Comments.css";
import firstReview from "img/firstReview.svg";
import Back from "img/chevron-back.svg";
import VerificationStar from "img/verificationStar.svg";
import InactiveStarSmall from "components/InactiveStarSmall";
import InactiveStar from "components/InactiveStar";
import ActiveStar from "components/ActiveStar";
import ActiveStarSmall from "components/ActiveStarSmall";

import {
  useCollectionData,
  useCollection,
} from "react-firebase-hooks/firestore";
import { Rating } from "react-simple-star-rating";
import { Context } from "../..";
import firebase from "firebase/compat";

const Comments = ({ go, group, user, rateResult, chosenGroup }) => {
  const [review, addReview] = useState("");
  const [hover, setHover] = useState();
  const [rating, setRating] = useState(0);
  const [textReply, setTextReply] = useState();
  const commentsMainRef = useRef(null);
  const commentsHeaderRef = useRef(null);
  const plural = require("plural-ru");

  const { firestore } = useContext(Context);

  const [messages, loading] = useCollectionData(
    firestore.collection(group?.id + "")
  );
  const [messagesId, loadingMesId] = useCollection(
    firestore.collection(group?.id + "")
  );
  let messagesIdMass = [];

  messagesId?.forEach((element, index) => {
    messagesIdMass.push(element.id);
  });

  useEffect(() => {
    if (review === "inactive" || review === "") return;

    const handleClick = (e) => {
      if (
        commentsMainRef.current.contains(e.target) ||
        commentsHeaderRef.current.contains(e.target)
      ) {
        handleBlockComment();
      }
    };

    document.addEventListener("click", handleClick);

    return () => {
      document.removeEventListener("click", handleClick);
    };
  }, [review]);

  const addComment = async (event) => {
    firestore.collection(group.id + "").add({
      uid: user.id,
      displayName: user.first_name + " " + user.last_name[0] + ".",
      displayPhoto: user.photo_100,
      text: event.target[0].value,
      createdAt: firebase.firestore.FieldValue.serverTimestamp(),
      rating: rating,
    });

    addReview(review === "active" ? "inactive" : "active");
    event.preventDefault();
  };

  const addReply = async (event) => {
    firestore.collection(group.id + "");

    // console.log(textReply);
    addReview(review === "active" ? "inactive" : "active");
    event.preventDefault();
  };

  const handleRating = (rate) => {
    setRating(rate);
  };

  const onPointerMove = (value) => {
    setHover(value);
  };

  const onPointerLeave = () => {
    setHover(rating);
  };

  const handleBlockComment = () => {
    addReview(review === "active" ? "inactive" : "active");
  };

  const handleComment = (event, comment) => {
    event.target.parentElement.children[2].textContent = comment;
    event.target.style.display = "none";
  };

  let months = [
    "Января",
    "Февраля",
    "Марта",
    "Апреля",
    "Мая",
    "Июня",
    "Июля",
    "Августа",
    "Сентября",
    "Октября",
    "Ноября",
    "Декабря",
  ];
  const getDate = (date) => {
    let newDate = new Date(date * 1000);
    return newDate.getDate() + " " + months[newDate.getMonth()];
  };

  const fillColorArray = [
    "#FF6B77",
    "#FF6B77",
    "#FFA735",
    "#37C99E",
    "#37C99E",
  ];

  if (loading || !group) {
    return (
      <div className="loader-wrap">
        <span className="loader"></span>
      </div>
    );
  }

  if (group.id == chosenGroup?.id) {
    console.log("EST");
  }

  return (
    <div className="comments">
      <div className="comments__header" ref={commentsHeaderRef}>
        <img
          src={Back}
          alt="Back"
          className="header-back"
          onClick={go}
          data-to="offers"
          data-page="comments"
        />
        <div className="member-logo">
          <img src={group?.photo_50} alt="Logo" className="member-logo-img" />
        </div>
        <div className="member-info">
          <div className="member-title">{group?.name}</div>
          <div className="member-about">
            <div
              className="member-rating"
              style={{
                color:
                  fillColorArray[
                    Math.floor(Number(rateResult[group?.id]?.count)) - 1
                  ],
              }}
            >
              {
                <ActiveStarSmall
                  color={Math.floor(Number(rateResult[group?.id]?.count))}
                  height={14}
                  style={{ marginTop: -2 }}
                />
              }
              {rateResult[group?.id]?.count}
            </div>
            <div className="member-reviews">
              {messages.length +
                " " +
                plural(messages.length, "отзыв", "отзывa", "отзывов")}
            </div>
            <div className="member-delimetr"></div>
            <div className="member-representatives">Есть представители</div>
          </div>
        </div>
      </div>
      <div className="wrap"></div>
      <div className="comments__main" ref={commentsMainRef}>
        {messages.length >= 1 ? (
          messages.map((comment, index) => (
            <div className="comments__item-wrap" key={index}>
              <div className="comments__item">
                <div className="comments__item-avatar">
                  <img
                    src={comment.displayPhoto}
                    className="comments__item-avatar-img"
                  />
                </div>
                <div className="comments__item-other">
                  <div className="comments__item-name">
                    {comment.displayName}
                  </div>
                  <div className="comments__item-dateAndRate">
                    <Rating
                      size={16}
                      readonly
                      initialValue={comment.rating}
                      emptyIcon={<InactiveStarSmall />}
                      fillIcon={
                        <ActiveStarSmall color={new Date(comment.rating)} />
                      }
                    />
                    <div className="comments__item-date">
                      {getDate(comment.createdAt?.seconds)}
                    </div>
                  </div>
                  <div className="comments__item-text">
                    {comment.text.length > 250
                      ? comment.text.slice(0, 250) + "..."
                      : comment.text}
                  </div>
                  {comment.text.length > 250 ? (
                    <div
                      className="comments__item-text-more"
                      onClick={() => handleComment(event, comment.text)}
                    >
                      Читать дальше
                    </div>
                  ) : (
                    ""
                  )}
                  {chosenGroup?.id == group?.id ? (
                    <div
                      className="comments__item-reply-btn"
                      onClick={() => {
                        setTextReply(comment);
                        handleBlockComment();
                      }}
                      id={
                        messagesIdMass.length > 1
                          ? messagesIdMass[index]
                          : undefined
                      }
                    >
                      Ответить
                    </div>
                  ) : (
                    ""
                  )}
                </div>
              </div>
              {comment.reply ? (
                <div className="comments__item-reply-wrap">
                  <img
                    className="comments__item-reply-avatar"
                    src={group?.photo_50}
                  />
                  <div className="comments__item-reply">
                    <div className="comments__item-reply-title">
                      <div className="comments__item-title-text">
                        {"Ответ " + group?.screen_name}
                      </div>
                      <img
                        src={VerificationStar}
                        className="comments__item-reply-title-img"
                      />
                    </div>
                    <div className="comments__item-reply-text">
                      {comment.reply}
                    </div>
                  </div>
                </div>
              ) : (
                ""
              )}
            </div>
          ))
        ) : (
          <div className="comments__firstReview">
            <img className="comments__firstReview-img" src={firstReview} />

            <div className="comments__firstReview-title">
              Ваш отзыв может стать первым
            </div>
            <div className="comments__firstReview-text">
              Об этой организации ещё никто не писал, и вы можете это изменить
            </div>
            <input
              type="button"
              value="Оставить отзыв"
              className="comments__firstReview-btn"
              onClick={handleBlockComment}
            />
          </div>
        )}
      </div>
      {messages.length >= 0 && chosenGroup?.id != group.id ? (
        <div className="comments__footer">
          <input
            type="button"
            value="Оставить отзыв"
            className="comments__footer-btn"
            onClick={handleBlockComment}
          />
        </div>
      ) : (
        ""
      )}

      <form
        className={`comments__feedback ${review}`}
        onSubmit={chosenGroup ? addReply : addComment}
      >
        {!chosenGroup ? (
          <>
            <div className="feedback-title">
              Ваша оценка и комментарий <br /> о компании Займер
            </div>
            <div className="feedback-rating">
              <Rating
                onClick={handleRating}
                onPointerMove={onPointerMove}
                onPointerLeave={onPointerLeave}
                transition
                className="feedback-rating-item"
                emptyIcon={<InactiveStar />}
                fillIcon={<ActiveStar rating={rating} hover={hover} />}
              />
            </div>
          </>
        ) : (
          ""
        )}
        <div className="feedback-textarea">
          <textarea className="feedback-textarea-input"></textarea>
        </div>
        <div className="feedback-submit">
          <input
            type="submit"
            value="Отправить"
            className="feedback-submit-btn"
          />
        </div>
        <div className="feedback-cancel">
          <input
            type="button"
            value="Отменить"
            className="feedback-cancel-btn"
            onClick={handleBlockComment}
          />
        </div>
      </form>
    </div>
  );
};

Comments.propTypes = {
  id: PropTypes.string.isRequired,
  go: PropTypes.func.isRequired,
};

export default Comments;
