import highChance from "img/highChance.svg";
import ActiveStarSmall from "components/ActiveStarSmall";

function OffersCard({
  rngValue,
  rateResult,
  go,
  groupId,
  groupAction,
  imgSrc,
}) {
  const fillColorArray = [
    "#FF6B77",
    "#FF6B77",
    "#FFA735",
    "#37C99E",
    "#37C99E",
  ];
  const plural = require("plural-ru");
  return (
    <div className="card" key={groupId}>
      <div className="block__header">
        <div className="block__logo">
          <img src={imgSrc} alt="Back" className="block__logo-img" />
        </div>
        <div className="block__chance">
          <div className="block__chance-total">
            <span>Высокий</span>
            <div className="block__chance-title">Шанс одобрения</div>
          </div>
          <img className="block__chance-img" src={highChance} />
        </div>
      </div>
      <form action={groupAction} target="_blank" className="block__btns">
        <input
          type="submit"
          value={"Получить " + rngValue.toLocaleString("ru") + " ₽"}
          className="block__btns-getMoney"
        />

        <div
          type="button"
          className="block__btns-reviews"
          id={groupId}
          onClick={go}
          data-to="comments"
          style={{
            color:
              fillColorArray[Math.floor(Number(rateResult[groupId].count)) - 1],
          }}
        >
          {
            <ActiveStarSmall
              color={Math.floor(Number(rateResult[groupId].count))}
              height={14}
              style={{ marginTop: -2 }}
              id={groupId}
            />
          }
          {rateResult[groupId].count}
          <div className="block__btns-reviews-count" id={groupId}>
            {rateResult
              ? rateResult[groupId].length +
                " " +
                plural(rateResult[groupId].length, "отзыв", "отзывa", "отзывов")
              : ""}
          </div>
        </div>
      </form>
    </div>
  );
}
export default OffersCard;
