interface ClassNamesDictionary {
  [index: string]: boolean | undefined | null;
}

type ClassName = string | number | ClassNamesDictionary | boolean | undefined | null;

export function classNames(...args: ClassName[]): string {
  const result: string[] = [];

  args.forEach((item): void => {
    if (!item) {
      return;
    }

    switch (typeof item) {
      case 'string':
        result.push(item);
        break;

      case 'object':
        Object.keys(item).forEach((key: string) => {
          if (item[key]) {
            result.push(key);
          }
        });
        break;

      default:
        result.push(`${item}`);
    }
  });

  return result.join(' ');
}
