import React, { useState, useEffect, useContext } from "react";
import bridge from "@vkontakte/vk-bridge";
import {
  View,
  AdaptivityProvider,
  AppRoot,
  ConfigProvider,
  SplitLayout,
  SplitCol,
} from "@vkontakte/vkui";
import "@vkontakte/vkui/dist/vkui.css";

import Home from "./panels/Home";
import Offers from "./panels/Offers";
import Comments from "./panels/Comments";
import Membership from "./panels/Membership";

import { useCollectionData } from "react-firebase-hooks/firestore";
import { Context } from ".";

const App = () => {
  const [scheme, setScheme] = useState("bright_light");
  const [activePanel, setActivePanel] = useState("home");

  const [rngValue, setRngValue] = useState([50000]);
  const [user, setUser] = useState([]);
  const [group, setGroup] = useState(null);
  const [checkUser, setCheckUser] = useState(false);
  const [trueUser, setTrueUser] = useState();
  const [activeOrganization, setActiveOrganization] = useState();
  const [adminsGroup, setAdminsGroup] = useState([]);
  const members = [129542706, 55652521, 42172377, 139111941, 217184645];
  const { firestore } = useContext(Context);
  const [messages1] = useCollectionData(firestore.collection("129542706"));
  const [messages2] = useCollectionData(firestore.collection("55652521"));
  const [messages3] = useCollectionData(firestore.collection("42172377"));
  const [messages4] = useCollectionData(firestore.collection("139111941"));
  const [chosenGroup, setChosenGroup] = useState();

  const messages = [messages1, messages2, messages3, messages4];
  const rateResult = {
    129542706: {},
    55652521: {},
    42172377: {},
    139111941: {},
  };

  const createRate = async () => {
    for (let i = 0; i < messages?.length; i++) {
      let summ = 0;
      for (let j = 0; j < messages[i]?.length; j++) {
        summ += messages[i][j]?.rating;
        if (j == messages[i]?.length - 1) {
          rateResult[members[i]].count = (summ / messages[i]?.length).toFixed(
            1
          );
          rateResult[members[i]].length = messages[i]?.length;
        }
      }
    }
  };
  createRate();
  const handleSumChange = (rngValue) => {
    setRngValue(+rngValue);
  };

  useEffect(() => {
    bridge.subscribe(({ detail: { type, data } }) => {
      if (type === "VKWebAppUpdateConfig") {
        setScheme(data.scheme);
      }
    });

    bridge.send("VKWebAppSetSwipeSettings", { history: true });

    async function fetchUser() {
      const user = await bridge.send("VKWebAppGetUserInfo").then((data) => {
        if (data.id) {
          setUser(data);
        }
      });
    }
    fetchUser();
  }, []);

  useEffect(() => {
    if (checkUser) {
      async function fetchGroup() {
        const token = await bridge.send("VKWebAppGetAuthToken", {
          app_id: 51435408,
          scope: "groups",
        });
        const userGroup = await bridge.send("VKWebAppCallAPIMethod", {
          method: "groups.get",
          request_id: "getGroups",
          params: {
            filter: "admin",
            extended: "1",
            fields: "is_admin",
            v: "5.131",
            access_token: token.access_token,
          },
        });
        let adminsGroups = [];

        for (let i = 0; i < userGroup.response.items.length; i++) {
          for (let j = 0; j < members.length; j++) {
            if (userGroup.response.items[i].id == members[j]) {
              adminsGroups.push(userGroup.response.items[i]);
            }
          }
        }
        if (adminsGroups.length >= 1) {
          setTrueUser(true);
          setAdminsGroup(adminsGroups);
        } else {
          setTrueUser(false);
        }
      }
      fetchGroup();
    }
  }, [checkUser]);

  useEffect(() => {
    async function fetchGroupInfo() {
      const groups = await bridge
        .send("VKWebAppGetGroupInfo", {
          group_id: +activeOrganization,
        })
        .then((data) => {
          if (data.id) {
            setGroup(data);
          }
        })
        .catch((error) => {
          console.log(error);
        });
    }
    if (activeOrganization) {
      fetchGroupInfo();
    }
  }, [activeOrganization]);

  const go = (e) => {
    if (e.target.id) {
      setActiveOrganization(e.target.id);
    }
    if (e.currentTarget.dataset.page === "comments") {
      setActiveOrganization(null);
      setGroup(null);
    }
    if (e.currentTarget.dataset.page === "membership") {
      setActiveOrganization(chosenGroup.id);
    }
    setActivePanel(e.currentTarget.dataset.to);
  };

  return (
    <ConfigProvider scheme={scheme}>
      <AdaptivityProvider>
        <AppRoot>
          <SplitLayout>
            <SplitCol>
              <View activePanel={activePanel}>
                <Home
                  id="home"
                  go={go}
                  onChange={handleSumChange}
                  rngValue={rngValue}
                />
                <Offers
                  id="offers"
                  go={go}
                  rngValue={rngValue}
                  group={group}
                  rateResult={rateResult}
                />
                <Comments
                  id="comments"
                  go={go}
                  user={user}
                  group={group}
                  rateResult={rateResult}
                  setActiveOrganization={setActiveOrganization}
                  chosenGroup={chosenGroup}
                />
                <Membership
                  id="membership"
                  go={go}
                  setCheckUser={setCheckUser}
                  trueUser={trueUser}
                  adminsGroup={adminsGroup}
                  chosenGroup={chosenGroup}
                  setChosenGroup={setChosenGroup}
                />
              </View>
            </SplitCol>
          </SplitLayout>
        </AppRoot>
      </AdaptivityProvider>
    </ConfigProvider>
  );
};

export default App;
