import React, { useState, useEffect, useContext } from "react";
import bridge from "@vkontakte/vk-bridge";
import {
  View,
  AdaptivityProvider,
  AppRoot,
  ConfigProvider,
  SplitLayout,
  SplitCol,
} from "@vkontakte/vkui";
import "@vkontakte/vkui/dist/vkui.css";

import Home from "./panels/Home";
import Offers from "./panels/Offers";
import Comments from "./panels/Comments";
import Membership from "./panels/Membership";

import cardLogoZaimer from "img/zaimer.png";
import cardLogoMoneyMan from "img/moneyman.jpg";
import cardLogoWebBankir from "img/webbankir-logo.jpg";

import { useCollectionData } from "react-firebase-hooks/firestore";
import { Context } from ".";

const App = () => {
  const [activePanel, setActivePanel] = useState("home");

  const [rngValue, setRngValue] = useState([50000]);
  const [user, setUser] = useState([]);
  const [currentGroup, setCurrentGroup] = useState(null);
  const [checkUser, setCheckUser] = useState(false);
  const [trueUser, setTrueUser] = useState();
  const [activeOrganization, setActiveOrganization] = useState();
  const [adminsGroup, setAdminsGroup] = useState([]);
  const [chosenGroup, setChosenGroup] = useState();

  const { firestore } = useContext(Context);

  const groupReviews = [];
  const groups = [
    {
      id: 129542706,
      imgSrc: cardLogoZaimer,
      actionSrc: "https://www.zaymer.ru/",
    },
    {
      id: 55652521,
      imgSrc: cardLogoMoneyMan,
      actionSrc: "https://moneyman.ru/",
    },
    {
      id: 42172377,
      imgSrc: cardLogoWebBankir,
      actionSrc: "https://webbankir.com/",
    },
    {
      id: 139111941,
      imgSrc: "",
      actionSrc: "",
    },
  ];

  // Price change function
  const handleSumChange = (rngValue) => {
    setRngValue(+rngValue);
  };

  // Initialization of all reviews, for further processing
  const initReviews = async () => {
    for (let i = 0; i < groups.length; i++) {
      groupReviews.push(
        useCollectionData(firestore.collection(groups[i].id + ""))
      );
    }
  };
  initReviews();

  // Adding fields to groups, average rating and total number of reviews
  const createRate = async () => {
    for (let i = 0; i < groupReviews?.length; i++) {
      let summ = 0;
      groups[i].reviewsCount = groupReviews[i][0]?.length;
      for (let j = 0; j < groupReviews[i][0]?.length; j++) {
        summ += groupReviews[i][0][j]?.rating;
        if (j == groupReviews[i]?.length - 1) {
          groups[i].averageRating = (summ / groupReviews[i]?.length).toFixed(1);
        }
      }
    }
  };
  createRate();

  // User initialization
  useEffect(() => {
    async function fetchUser() {
      const user = await bridge.send("VKWebAppGetUserInfo").then((data) => {
        if (data.id) {
          setUser(data);
        }
      });
    }
    fetchUser();
  }, []);

  // Finding group admin rights for a user
  useEffect(() => {
    if (checkUser) {
      async function fetchGroup() {
        const token = await bridge.send("VKWebAppGetAuthToken", {
          app_id: 51435408,
          scope: "groups",
        });
        const userGroup = await bridge.send("VKWebAppCallAPIMethod", {
          method: "groups.get",
          request_id: "getGroups",
          params: {
            filter: "admin",
            extended: "1",
            fields: "is_admin",
            v: "5.131",
            access_token: token.access_token,
          },
        });
        let adminsGroups = [];

        for (let i = 0; i < userGroup.response.items.length; i++) {
          for (let j = 0; j < groups.length; j++) {
            if (userGroup.response.items[i].id == groups[j].id) {
              adminsGroups.push(userGroup.response.items[i]);
            }
          }
        }
        if (adminsGroups.length >= 1) {
          setTrueUser(true);
          setAdminsGroup(adminsGroups);
        } else {
          setTrueUser(false);
        }
      }
      fetchGroup();
    }
  }, [checkUser]);

  // Group initialization
  useEffect(() => {
    async function fetchGroupInfo() {
      const groups = await bridge
        .send("VKWebAppGetGroupInfo", {
          group_id: +activeOrganization,
        })
        .then((data) => {
          if (data.id) {
            setCurrentGroup(data);
          }
        })
        .catch((error) => {
          console.log(error);
        });
    }
    if (activeOrganization) {
      fetchGroupInfo();
    }
  }, [activeOrganization]);

  // Page navigation function
  const go = (e) => {
    if (e.target.id) {
      setActiveOrganization(e.target.id);
    }

    if (e.currentTarget.dataset.page === "comments") {
      setActiveOrganization(null);
      setCurrentGroup(null);
    }
    if (e.currentTarget.dataset.page === "membership") {
      setActiveOrganization(chosenGroup.id);
    }
    setActivePanel(e.currentTarget.dataset.to);
  };

  return (
    <ConfigProvider>
      <AdaptivityProvider>
        <AppRoot>
          <SplitLayout>
            <SplitCol>
              <View activePanel={activePanel}>
                <Home
                  id="home"
                  go={go}
                  onChange={handleSumChange}
                  rngValue={rngValue}
                />
                <Offers
                  id="offers"
                  go={go}
                  rngValue={rngValue}
                  groups={groups}
                />
                <Comments
                  id="comments"
                  go={go}
                  user={user}
                  currentGroup={currentGroup}
                  groups={groups}
                  setActiveOrganization={setActiveOrganization}
                  chosenGroup={chosenGroup}
                />
                <Membership
                  id="membership"
                  go={go}
                  setCheckUser={setCheckUser}
                  trueUser={trueUser}
                  adminsGroup={adminsGroup}
                  chosenGroup={chosenGroup}
                  setChosenGroup={setChosenGroup}
                />
              </View>
            </SplitCol>
          </SplitLayout>
        </AppRoot>
      </AdaptivityProvider>
    </ConfigProvider>
  );
};

export default App;
